import { useState } from "react";

import ChatBox from "../ChatBox/ChatBox";
import TableCard from "../table/TableCard";

const Support = ()=>{

   const [open,setOpen]=useState(false);
   const handleClose = () => {setShow(false);setEdit(false)};
   const handleShow = () => setShow(true);
   const [show, setShow] = useState(false);
   const [isShowLoader,setShowLoader] = useState(false);

   const Open=()=>{
      setOpen(!open);
   }
   const ProfileImage=image=>{

      return <img 
              style={{
                width:"30px",
                height:"30px"
              }}
          src={image} alt="profile"/>
    }

    const Buttons =(id,username)=>{
      return <> 
           <button style={{color:"blue"}} onClick={()=>EditUser(id)} className="fa fa-edit btn"></button> 

             <div class="custom-control custom-switch">
           <input type="checkbox"  onChange={(e)=>DisableUser(id,e.target.checked)} class="custom-control-input" id={"customSwitch"+id}/>
            <label class="custom-control-label" for={"customSwitch"+id}>Off/On</label>
           </div>
       </>
         }

         const EditUser=(id)=>{
            setUserId(id);
           setEdit(true)
       }
       const Back=()=>{
         setUserId('')
         setEdit(false);
       }

       const DisableUser =(id,event)=>{
           if(event){
               //activate account
               notify('activating account for '+id)

           }else{
               //deactivate account
               notify('Deactivating account for '+id)
           }

       }

   const columns = [
        {
          dataField: "#Id",
          text: "Id",

        },
        {
          dataField: "image",
          text: "Profile",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "level",
            text: "Administartor Type",


        },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
         {
           "#Id":"33444",
            image:ProfileImage('https://media.istockphoto.com/photos/senior-man-remembering-picture-id1166588777?b=1&k=20&m=1166588777&s=170667a&w=0&h=lNpNLSTkPOAvvo4TwEbUQa_p0IjFUQ6K5bjodzSzJok='),
            writerName:"Airo",
            email:"airotony8@",
            status:"status",
            level:"level",
            Action:Buttons(5,"airo")
         }
      ]
 return <>  
<div className="container card">
<TableCard products={products} columns={columns} title={"Admin-Strators"}/>
</div>
      <button onClick={()=>Open()}>Open</button>
       <ChatBox 
         open={open}
         SetOpen={Open}
        />
   </>;
}

export default Support;
