import { useState } from "react";
import TableCard from "src/views/table/TableCard";
import SingeleTask from "../../TaskDetails/SingleTaskDetails";

const CancelledTask = () =>{

  const [taskDetails,setTaskDetails] = useState(false);
  const [taskId,setTaskId]=useState('');

  const CloseTask=()=>{
    setTaskDetails(false);
  }

  const TaskDetails=(taskId)=>{
    setTaskId(taskId);
     setTaskDetails(true);
  }

    const columns = [
        {
          dataField: "#taskId",
          text: "#taskId",

        },
        {
          dataField: "titke",
          text: "Title",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Writer Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "category",
            text: "Category",


        },
        {

          dataField: "Cdate",
          text: "Cancelled-Date",


      },
      {

        dataField: "ddate",
        text: "Deadline",


      },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
        {
          "#taskId":"686787678",
          title:"iuytfrdfgh",
          writerName:"wilson",
          email:"wilson@gmail.com",
          status:"in-progress",
          category:"Lorem",
          Cdate:'2011-9-10',
          ddate:"2011-8-8",
          Action:<button onClick={()=>TaskDetails(1)} className="btn btn-primary">Details</button>

       }
      ]
    return <>
            {!taskDetails && <TableCard 
             products={products}
             columns={columns}
              title={"CANCELLED TASK"}
             />}


{taskDetails &&<SingeleTask id={taskId} closeTask={CloseTask} isAccept={false}/>}
          </>

}

export default CancelledTask;
