import { useState } from "react";
import ChatBox from "src/views/ChatBox/ChatBox";
import TableCard from "src/views/table/TableCard";
import SingeleTask from "../../TaskDetails/SingleTaskDetails";

const TaskBeingRedone = ()=>{
  const [open,setOpen]=useState(false);
  const [taskDetails,setTaskDetails] = useState(false);
  const [taskId,setTaskId]=useState('');

  const Open=()=>{
    setOpen(!open);
 }

 const CloseTask=()=>{
  setTaskDetails(false);
}

const TaskDetails=(taskId)=>{
  setTaskId(taskId);
   setTaskDetails(true);
}

    const columns = [
        {
          dataField: "#taskId",
          text: "#taskId",

        },
        {
          dataField: "title",
          text: "Title",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Writer Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "category",
            text: "Category",


        },
        {

          dataField: "sdate",
          text: "Submited-Date",


      },
      {

        dataField: "ddate",
        text: "Deadline",


      },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
          {
            "#taskId":"7888888",
            title:"Lorem ipsume",
            writerName:"John ",
            email:"airotony8@gmail.com",
            status:"Revision",
            category:"Lorem",
            sdate:"2018-9-10",
            ddate:"2019-10-10",
            Action:<><button onClick={()=>TaskDetails(2)} className="btn btn-info">Task Details</button>
                      <button onClick={()=>Open()} style={{marginLeft:"2px"}}  className="btn btn-success fa fa-comment"></button>
              </>

          }
      ]
    return <>
            {!taskDetails && <> <TableCard 
             products={products}
             columns={columns}
              title={"TASK BEING REVISED"}
             />

             <ChatBox open={open} SetOpen={Open}/>
             </>
            }

            {taskDetails &&<SingeleTask id={taskId} closeTask={CloseTask} isAccept={false}/>}
          </>

}

export default TaskBeingRedone;
