import { useState } from "react";
import TableCard from "src/views/table/TableCard";
import SingeleTask from "../../Task/TaskDetails/SingleTaskDetails";

const PendingPayments= ()=>{

  const [isShowSingleTask,setIsShowSingleTask]=useState(false);
  const [taskId,setTaskId]=useState('');
  const [markedOrders,setMarkedOrders] = useState([])

  const ShowSingleTasks =taskid=>{
    setTaskId(taskid);
  // setIsShowInvoice(false);
   setIsShowSingleTask(true);
  }
  const CloseSingleTask=()=>{
    setTaskId('');
    // setIsShowInvoice(false);
     setIsShowSingleTask(false);
  }

  const HandleSelected =(id,invoice,event)=>{
      if(event.target.checked){
        let newMarked = [...markedOrders];
        newMarked.push({invoice:invoice,taskId:id})
        //[i][e.target.name] = e.target.value;
       setMarkedOrders(newMarked);

       alert(JSON.stringify(markedOrders))
      }else {alert(markedOrders.indexOf(id))};
  }

  const Button=id=>{
    return <>
        <button style={{marginRight:"5px"}} className="btn btn-info">Mark Paid</button>
    <button style={{

              color:"#000",

  }} onClick={()=>ShowSingleTasks(id)} className="btn btn-success fa fa-history"></button>
  </>
  }
    const columns = [
      {
        dataField: "check",
        text: <span className="fa fa-check badge badge-success"></span>,

      },
        {
          dataField: "#taskId",
          text: "#taskId",

        },

        {
          dataField: "invoiceId",
          text: "#Invoice Id",

        },
        {
          dataField: "email",
          text: "Writer email",

        },
        {

            dataField: "status",
            text: "status",


        },

        {

          dataField: "pdate",
          text: "Expected-Day-Pay",


      },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
          {
            check:<input onChange={(e)=>HandleSelected(1,'#738829',e)} type="checkbox"/>,
            "#taskId":"#738829",
            invoiceId:"#55445555",
            email:"airotony8@gmail.com",
            status:<span className="badge badge-warning">unpaid</span>,
            pdate:"2021-7-10",
            Action:Button(1)
          }

      ]
    return <>
            {!isShowSingleTask && <div className="container card">
             <TableCard 
             products={products}
             columns={columns}
              title={"PENDING PAYMENTS"}
             />
             </div>}

            {isShowSingleTask && <SingeleTask id={taskId} closeTask={CloseSingleTask} isAccept={false}/>}
          </>

}
export default PendingPayments;
