import Dropzone from './dropzone/Dropzone';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { useState } from 'react';
import swal from 'sweetalert';
import { Modal ,Button} from 'react-bootstrap';

const PostTask=()=>{

  const [validFiles, setValidFiles] = useState([]);
  const [Show,setShow]= useState(false);
  const [prog,setProg]= useState(0);


  const HandleClose = () =>{
    setShow(false);
  }


 const HandlePostTask=()=>{


setShow(true);
var number = prog;
var progress = setInterval(() => {
   if(prog == 100){
     clearInterval(progress);
     setTimeout(() => {
       setShow(false);
       swal("Upload Success","file uploaded succesfully","success");
     }, 1500);
   };




number = number +2;
   console.log(number);
   //setProg(prog);
}, 1500);



  }


    return <>
    <div  className="container card">
             <form style={{
                    marginTop:"30px",
                    marginBottom:"30px"
             }}>
  <div className="form-row">
    <div className="form-group col-md-6">
      <label for="inputEmail4">Title</label>
      <input type="text" className="form-control" id="inputEmail4" placeholder="Title"/>
    </div>
    <div className="form-group col-md-6">
      <label for="inputPassword4">Category</label>
       <select className="form-control">
           <option selected>chose Category</option>
       </select>
    </div>
  </div>

  <div className="form-row">
    <div className="form-group col-md-6">
      <label for="inputCity">Time</label>
      <input type="time" className="form-control" id="inputCity"/>
    </div>
    <div className="form-group col-md-4">
      <label for="inputState">Expected date</label>
        <input className="form-control" type="date"/>
    </div>
    <div className="form-group col-md-2">
      <label for="inputZip">Price ($..dollars)</label>
      <input type="number" className="form-control" min="0.00" max="10000.00" step="0.01" placeholder="price in dollars" />

    </div>
  </div>

  <div className="form-row">
  <div style={{display:"block"}} class="form-group col-md-12">
      <Dropzone validFiles={validFiles} setValidFiles={setValidFiles}/>
</div>
  </div>

  <div className="form-row">
     <div className="form-group col-md-12">
     <label for="inputZip">Descripe task Here(..Task Details)</label>
   <CKEditor

       editor={ ClassicEditor }
      // data={message}

       onReady={ editor => {
           // You can store the "editor" and use when it is needed.
           //console.log( 'Editor is ready to use!', editor );
           editor.editing.view.change((writer) => {
            writer.setStyle(
                "height",
                "300px",
                editor.editing.view.document.getRoot()
            );
            });
       } }
       onChange={ ( event, editor ) => {
           const data = editor.getData();

           //setMessage(data);
       } }

   />
     </div>
  </div>

  <button onClick={()=>HandlePostTask()}  type="button" className="btn btn-primary">Post New Task</button>
</form>
</div>





<Modal show={Show} onHide={HandleClose}>
<Modal.Header>
  <Modal.Title>Uploading Task</Modal.Title>
</Modal.Header>
<Modal.Body>
<small>Upload Progress 20%</small>
                      <div className="progress mb-3" style={{height:"15px"}}>
                        <div className="progress-bar bg-primary" role="progressbar" style={{width:prog+"%"}} aria-valuenow="72" aria-valuemin="0" aria-valuemax="100"></div>
                      </div>
</Modal.Body>

</Modal>


          </>;
}

export default PostTask;
