import { useEffect, useState } from "react";
import CategoryEdit from "src/views/base/Users/writers/editCategory";
import Profile from "src/views/base/Users/writers/profile/profile";
import TableCard from "src/views/table/TableCard";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import swal from 'sweetalert';
import ApiRequest from "src/Api/Data/requestApi";

const ReasignUser = ({setBack,buttonName,title,status,createDate,endDate,description})=>{
       const [edit,setEdit] = useState(false);
       const [category,setCategory] = useState([]);
       const [userId,setUserId]=useState('');

       const notify = (data) => toast(data);

       const [initialSelected,setInititialSelected] = useState([
        { label: "Short Stories.",value:"short"},
        { label: "Technically",value:"technical"},
        { label: "Novellas",value:"novels"},
        { label: "Scripts and Screenplays",value:"scripts"},
        { label: "True Crime. ...",value:"true"},
        { label: " Auto/Biography.",value:"auto"},
       ]);
       const options = [
        { label: "Short Stories.",value:"short"},
        { label: "Technically",value:"technical"},
        { label: "Novellas",value:"novels"},
        { label: "Scripts and Screenplays",value:"scripts"},
        { label: "True Crime. ...",value:"true"},
        { label: " Auto/Biography.",value:"auto"},

        ];

        useEffect(()=>{
        setBack(true);
        },[])
        const ReAsignTask=(id)=>{
            swal(`Are You Sure ${buttonName} This Task!?`, {
                buttons: {
                  cancel: "Dont Proceed!",
                  delete: {
                    text: buttonName,
                    value: "reasign",
                  },

                },
              })
              .then((value) => {
                switch (value) {



                  case "reasign":
                    swal(`task`, "Re-assigned Successfully", "success");
                    break;

                  default:
                    swal("Process Canceled Succesfully!");
                }
              });
        }

       /* const DisableUser =(id,event)=>{
            if(event){
                //activate account
                notify('activating account for '+id)

            }else{
                //deactivate account
                notify('Deactivating account for '+id)
            }

        }*/
        const EditUser=(id)=>{
            setUserId(id);
            setEdit(true)
            setBack(false)
       }

        const Back=()=>{
            setUserId('')
            setEdit(false);
            setBack(true);
          }

    const ViewUser=(id)=>{

    }

    const SingleTask=(id)=>{

    }
    const statusMode=(status)=>{
      if(status === "new"){
       return <span className="badge badge-info">{status}</span>
      }
      if(status === "cancelled"){
       return <span className="badge badge-warning">{status}</span>
      }
    if(status === "completed"){
      return <span className="badge badge-success">{status}</span>
    }
    if(status === "in-progress"){
      return <span className="badge badge-primary">{status}</span>
    }
}

const BlockUserPermanet=(id,username)=>{
  swal("You Are About To Permanently Block "+username+"!?", {
      buttons: {
        cancel: "Dont Block!",
        delete: {
          text: "Block User",
          value: "delete",
        },

      },
    })
    .then(async (value) => {
      switch (value) {



        case "delete":
            const result = await ApiRequest.BlockUser(id);
            if(result.data == true){
          swal(`${username}`, "Deleted Succesfully", "success");
         // LoadWriters();
            }
          break;

        default:
          swal("Process Canceled Succesfully!");
      }
    });
}

const DisableUser =(id,username)=>{
swal("","Unblock "+username+"!?","warning", {
  buttons: {
    cancel: "Cancel!",
    delete: {
      text: "Unblock User",
      value: "unblock",
    },

  },
})
.then(async (value) => {
  switch (value) {



    case "unblock":
      const result = await ApiRequest.unblockUser(id);

     if(result.data == true){
      swal(`${username}`, "unblocked Succesfully", "success");
      //LoadWriters();
     }
      break;

    default:
      swal("Process Canceled Succesfully!");
  }
});
}

    const columns=[
        {
            dataField: "username",
            text: "username",

          },
          {
            dataField: "email",
            text: "email",

          },
          {
            dataField: "action",
            text: "Action",

          },
    ]
    const products=[
