import React, { lazy } from 'react'
//import Tables from '../table/table.js'
import TableCard from '../table/TableCard.js';
import { Dropdown } from "react-bootstrap";


const WidgetsDropdown = lazy(() => import('../widgets/WidgetsDropdown.js'))

const columns = [
  {
    dataField: "#Order",
    text: "Order Id",

  },
  {
    dataField: "Title",
    text: "Title",

    sort: true
  },
  {
    dataField: "status",
    text: "Status",

  },
  {
    dataField: "created",
    text: "Created On",

  },
  {

      dataField: "deadline",
      text: "Deadline",


  },
  {
      dataField: "Action",
      text: "Action",

    }
];

const style={
  button:{
    width:"90%",
    border:"0px",
    backgroundColor:"white",
    border:"1px solid blue",
    height:"40px",
    borderRadius:"4px",
    marginTop:"2px",
    marginBottom:"2px",

  }
}
const Buttons=()=>{
  return <React.Fragment>

    <Dropdown>
  <Dropdown.Toggle variant="success" id="dropdown-basic">
    Dropdown Button
  </Dropdown.Toggle>

  <Dropdown.Menu>
    <div style={{display:"flex",flexDirection:"column",justifyItems:"center"}}>
    <button style={style.button}>Views Task</button>
   <button>Writer</button>
   <button>accept</button>
    <button>Reject</button>
    </div>

  </Dropdown.Menu>
</Dropdown>

           </React.Fragment>


}

const products = [
  {
    '#Order': "#7882992",
    price: 100,
    Title: 10,
    status: "japan",
    created:"2020-7-19",
    deadline:"2019-18-10",
    Action:Buttons()

  },

];


const Dashboard = () => {

  return (
    <>
      <WidgetsDropdown />
      <div className="container card">


      <TableCard title={"Writers Task Request"} columns={columns} products={products}/>
      </div>

    </>
  )
}


export default Dashboard
