//import { cilSortNumericDown } from "@coreui/icons";
import { useState } from "react";
import ModalCard from "src/views/modal/Modal";
import TableCard from "src/views/table/TableCard";
import swal from "sweetalert";

const NewTaskSubmited = ()=>{

  const [Show,setShow]=useState(false);
  const [task,setTask]=useState([]);

  const OpenModal =(id,invoice)=>{
      setShow(true)

      setTask({taskid:id,invoice:invoice});
  }

  const Close=()=>{
      setShow(false);
      setTask([]);
  }

  const Content =()=>{
      return <>
 <ul className="list-group">
  <li className="list-group-item d-flex justify-content-between align-items-center">
    Cras justo odio
    <span className="badge badge-primary badge-pill">14</span>
  </li>
  <li className="list-group-item d-flex justify-content-between align-items-center">
    Dapibus ac facilisis in
    <span className="badge badge-primary badge-pill">2</span>
  </li>
  <li className="list-group-item d-flex justify-content-between align-items-center">
    Morbi leo risus
    <span className="badge badge-primary badge-pill">1</span>
  </li>
</ul>
       </>
  }

    const OpenMoreModal=(id,invoice)=>{
      swal(`INVOICE +${invoice}`,{
            buttons:{
                file:{
                    text:"SUBMITED FILES",
                    value:"file",
                    className:"btn btn-outline-info"
                },

                details:{
                    text:"ORDER DETAILS",
                    value:"details",
                    className:"btn btn-info"
                },
            }
      })
      .then((value)=>{
          switch (value) {
              case "file":
                  OpenModal(id,invoice);
                  break;

                    case "details":

                        break;  

              default:
                  break;
          }
      })
    }
    const Buttons=(id,invoice)=>{
        return <button onClick={()=>OpenMoreModal(id,invoice)} className="btn btn-primary">More</button>
     }

     const Btns=()=>{
        return <> 
                  <button className="btn btn-info">REQUEST REDONE</button>
                  <button className="btn btn-success">ACCEPT ORDER</button>

              </>
     }

    const columns=[
        {
            dataField: "#taskId",
            text: "#taskId",

          },
          {
            dataField: "username",
            text: "writername",

          },
          {
            dataField: "email",
            text: "writer email",

          },
          {
            dataField: "taskTitle",
            text: "Task Title",

          },
          {
            dataField: "sdate",
            text: "submited Date",

          },
          {
            dataField: "edate",
            text: "DeadLine",

          },
          {
            dataField: "action",
            text: "Actions",

          },
    ]
    const Products=[
                {
                    "#taskId":"98272282",
                    username:"airo",
                    email:"airotony8@gmail.com",
                    taskTitle:"ggdttsssjjsbsbs",
                    sdate:"2020-8-9",
                    edate:"2020-7-8",
                    action:Buttons(5,"#55277772")
                }
    ]

    return <>
           <ModalCard
            Show={Show} Close={Close}
            showBtn={false}
            Buttons={Btns}
            content={Content}
             />
          <TableCard products={Products} columns={columns} title={"NEW SUBMITED TASK"}/>
     </>


}

export default NewTaskSubmited;
