import { useState } from "react";
import ChatBox from "src/views/ChatBox/ChatBox";
import TableCard from "src/views/table/TableCard";
import swal from "sweetalert";
import SingeleTask from "../../TaskDetails/SingleTaskDetails";

const TaskInProgress=()=>{

  const [open,setOpen]=useState(false);
  const [taskDetails,setTaskDetails] = useState(false);
  const [taskId,setTaskId]=useState('');


  const Open=()=>{
     setOpen(!open);
  }
  const CloseTask=()=>{
    setTaskDetails(false);
  }



  const CancelOrder=(orderId)=>{
    swal("YOU ARE ABOUT TO CANCEL ONGOING ORDER",{
       buttons:{
         cancel:"NO",
         yes:{
           text:"YES",
           value:"yes"
         }
       }
    })
    .then((value)=>{
          switch (value) {
            case 'yes':
                  swal("Order Cancelled","succesfully","success");
              break;

            default:

              break;
          }
    })
  }

     const columns = [
        {
          dataField: "#taskId",
          text: "#taskId",

        },
        {
          dataField: "title",
          text: "Title",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Writer Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "category",
            text: "Category",


        },
        {

          dataField: "Adate",
          text: "Accepted-Date",


      },
      {

        dataField: "ddate",
        text: "Deadline",


      },
        {
            dataField: "Action",
            text: "Action",

          }
      ];

      const Buttons = ()=>{
             return <>
                   <button onClick={()=>Open()} style={{marginRight:"3px"}} className="btn btn-primary fa fa-comment"></button>
                   <button onClick={()=>CancelOrder(1)}  style={{marginRight:"3px"}} className="btn btn-warning">Cancel</button>
                   <button onClick={()=>CancelOrder(1)} className="btn btn-info">Details</button>
                   </>
      }
      const products=[
        {
           "#taskId":"686787678",
           title:"iuytfrdfgh",
           writerName:"wilson",
           email:"wilson@gmail.com",
           status:"in-progress",
           category:"Lorem",
           Adate:'2011-9-10',
           ddate:"2011-8-8",
           Action:Buttons()

        }
    ]
    return <>
            {!taskDetails && <> <TableCard 
             products={products}
             columns={columns}
              title={"ORDERS-IN-PROGRESS"}
             />
             <ChatBox open={open} SetOpen={Open}/>
             </>
             }
             {taskDetails &&<SingeleTask id={taskId} closeTask={CloseTask} isAccept={false}/>}
          </>

}

export default TaskInProgress;
