import { useState } from "react";
import TableCard from "src/views/table/TableCard";
import Invoice from "../../Invoice/Invoice";
import SingeleTask from "../../Task/TaskDetails/SingleTaskDetails";

const History = ()=>{
    const [isShowInvoice,setIsShowInvoice] = useState(false);
    const [isShowSingleTask,setIsShowSingleTask]=useState(false);
    const [taskId,setTaskId]=useState('');
    const [invoiceId,setInvoiceId]= useState('');

     const ShowInvoice=invoiceid=>{
         setInvoiceId(invoiceid);
         setIsShowInvoice(true);
         setIsShowSingleTask(false);
     }
     const ShowSingleTasks =taskid=>{
       setTaskId(taskid);
      setIsShowInvoice(false);
      setIsShowSingleTask(true);
     }

     const CloseInvoice=()=>{
      setInvoiceId('');
      setTaskId('');
      setIsShowInvoice(false);
      setIsShowSingleTask(false);
     }


  const Button=id=>{
    return <button style={{width:"100%",

              color:"#000"
  }} onClick={()=>ShowInvoice(id)} className="btn btn-success fa fa-history"></button>
  }
    const columns = [

        {
          dataField: "invoiceId",
          text: "#Invoice Id",

        },

        {

            dataField: "status",
            text: "status",


        },
        {

          dataField: "pdate",
          text: "Paid-Date",
       },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
        {
          invoiceId:"#21347yt",
          status:<span className="badge badge-success">paid</span>,
          pdate:"2020-4-8",
          Action:Button(5)
        }
      ]


    return <>
          {!isShowInvoice && !isShowSingleTask &&    <TableCard 
             products={products}
             columns={columns}
              title={"PAYMENT HISTORY"}
             />
          }

        {isShowInvoice && !isShowSingleTask && <Invoice InvoiceId={invoiceId} showSingleTask ={ShowSingleTasks} closeInvoice={CloseInvoice}/>}  

        {!isShowInvoice && isShowSingleTask && <SingeleTask id={taskId} closeTask={ShowInvoice} isAccept={false}/>}
          </>

}

export default History;
