import { useEffect, useState } from "react";
import CategoryEdit from "src/views/base/Users/writers/editCategory";
import Profile from "src/views/base/Users/writers/profile/profile";
import TableCard from "src/views/table/TableCard";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import swal from 'sweetalert';

const ReasignUser = ({setBack,buttonName,title,status,createDate,endDate,description})=>{
       const [edit,setEdit] = useState(false);
       const [category,setCategory] = useState([]);
       const [userId,setUserId]=useState('');

       const notify = (data) => toast(data);

       const [initialSelected,setInititialSelected] = useState([
        { label: "Short Stories.",value:"short"},
        { label: "Technically",value:"technical"},
        { label: "Novellas",value:"novels"},
        { label: "Scripts and Screenplays",value:"scripts"},
        { label: "True Crime. ...",value:"true"},
        { label: " Auto/Biography.",value:"auto"},
       ]);
       const options = [
        { label: "Short Stories.",value:"short"},
        { label: "Technically",value:"technical"},
        { label: "Novellas",value:"novels"},
        { label: "Scripts and Screenplays",value:"scripts"},
        { label: "True Crime. ...",value:"true"},
        { label: " Auto/Biography.",value:"auto"},

        ];

        useEffect(()=>{
        setBack(true);
        },[])
        const ReAsignTask=(id)=>{
            swal(`Are You Sure ${buttonName} This Task!?`, {
                buttons: {
                  cancel: "Dont Proceed!",
                  delete: {
                    text: buttonName,
                    value: "reasign",
                  },

                },
              })
              .then((value) => {
                switch (value) {



                  case "reasign":
                    swal(`task`, "Re-assigned Successfully", "success");
                    break;

                  default:
                    swal("Process Canceled Succesfully!");
                }
              });
        }

        const DisableUser =(id,event)=>{
            if(event){
                //activate account
                notify('activating account for '+id)

            }else{
                //deactivate account
                notify('Deactivating account for '+id)
            }

        }
        const EditUser=(id)=>{
            setUserId(id);
            setEdit(true)
            setBack(false)
       }

        const Back=()=>{
            setUserId('')
            setEdit(false);
            setBack(true);
          }

    const ViewUser=(id)=>{

    }

    const SingleTask=(id)=>{

    }
    const statusMode=(status)=>{
      if(status === "new"){
       return <span className="badge badge-info">{status}</span>
      }
      if(status === "cancelled"){
       return <span className="badge badge-warning">{status}</span>
      }
    if(status === "completed"){
      return <span className="badge badge-success">{status}</span>
    }
    if(status === "in-progress"){
      return <span className="badge badge-primary">{status}</span>
    }
}

    const columns=[
        {
            dataField: "username",
            text: "username",

          },
          {
            dataField: "email",
            text: "email",

          },
          {
            dataField: "action",
            text: "Action",

          },
    ]
    const products=[
        {
            username: "Wilson",
            email: "wilson@gmail.com",
            action:<>
                     <button onClick={()=>ReAsignTask(1)} style={{marginRight:"3px"}} className="btn btn-success fa fa-check"></button>
                     <button onClick={()=>EditUser(1)} className="btn btn-primary fa fa-user"></button>
                   </>

          },
    ]
    return  <> {!edit && 

     <div className="container">
    <div className="main-body">




          <div className="row gutters-sm">

            <div style={{marginTop:"20px"}} className="col-md-12">

              <div className="row gutters-sm">
                <div className="col-sm-6 mb-3">
                  <div className="card h-100">
                    <div className="card-body">
                      <h6 className="d-flex align-items-center mb-3"><i className="material-icons text-info mr-2">Orders </i>Details</h6>
                      <div className="form-group">
                           <label for="inputAddress2">Order Dtails</label>
                           <div class="container">

                            <div class="row">
                                <div class="col">
                                     Order Title
                                </div>
                              <div  class="col badge badge-info">
                                  {title}
                               </div>
                                 </div>
                            <div class="row">
                            <div class="col">
                                status
                              </div>
                             <div class="col">
                               {statusMode(status)}

                           </div>

                         </div>

                         <div class="row">
                           <div class="col">
                                creted on
                              </div>
                             <div class="col">
                               {createDate}

                           </div>
                           </div>
                           <div class="row">
                            <div class="col">
                                end Date
                              </div>
                             <div class="col">
                               {endDate}

                           </div>
                           </div>


                         <br/>
                         <div class="row">
                            <div class="col card">
                                <label>Description</label>
                                <div className="col-md-11" dangerouslySetInnerHTML={{ __html:description }} >

                           </div>
                              </div>

                         </div>


                       </div>

                         </div>

                    </div>
                  </div>
                </div>

                <div className="col-sm-6 mb-3">
                  <div className="card h-100">
                    <div className="card-body" style={{overflowY:"auto"}}>
                      <h6 className="d-flex align-items-center mb-3"><i className="material-icons text-info mr-2">System</i>Billing Sequence</h6>
                      <div className="form-group">
                           <label for="inputAddress2">Filters Users &nbsp;<span className="badge badge-warning">1 week</span></label>
                         <select className="form-control">
                             <option selected>
                                  users under assigned categorie
                             </option>
                             <option>
                                  All
                             </option>

                         </select>
                         </div>
                     <TableCard title={"AVAILABLE USERS"} products={products} columns={columns}/>

                    </div>

                  </div>
                </div>


              </div>



            </div>
          </div>

        </div>
    </div>}

    {edit && <Profile
       category={<CategoryEdit data={options} selected={category}  setSelected={setCategory}/>}
       existingCategories={initialSelected}
       back={Back}
       DisableUser={DisableUser}
       id={userId}
    /> }
    <ToastContainer/>
    </>
}

export default ReasignUser;
