import { useEffect, useState } from 'react';
import ApiRequest from 'src/Api/Data/requestApi';
import './style.css';
const ChatBox = ({open,SetOpen,socket,email,loged})=>{
    const [messages,setMessages] = useState([]);
    const [Loged,setLoged] = useState('');
    const [receiver,setReceiver] = useState('');
    const [message,setMessage] = useState('');
    const [profile,setProfile] = useState('');
    const [clientProfile,setClientProfile] = useState('');
    const [senderUsername,setUsername] = useState('');




    const loadChatClient =async  ()=>{

             var from = localStorage.getItem('mail');
             const result = await ApiRequest.loadChatUsers(from,email);

             console.log(result.data)


    }



    useEffect(()=>{
        if(open == true){
            var from = localStorage.getItem('mail');
            setLoged(from);
            setReceiver(email);

            loadChatClient();
        }    

    },[socket])



    const sendMessage =()=>{
        var body ={
            username:senderUsername,
            Imageurl:profile,
            text:message,
            to:receiver,
            from:Loged,

     }
      setMessages([...messages,[body]])

    }
   // alert(email)

   return <>
    <div className="chat-popup" style={{display:open?"block":"none",backgroundColor:"brown"}} id="myForm">

        <div style={{marginBottom:"5px"}} className="container">
        <div class="avatar">

            <img class="img-circle" style={{width:"40px",height:"40px",borderRadius:"20px"}}  src={"https://media.istockphoto.com/photos/elephants-fear-of-mice-picture-id155439372?b=1&k=20&m=155439372&s=170667a&w=0&h=4BMK9d2lxIvBFNRDx-T_JproEaxG2O1P38XudI2Wfkg="} />
           &nbsp; <span className="badge badge-warning">online</span>
            </div>
    <button type="button" style={{float:'right',color:"blue",
                   border:"1px solid green",
                   backgroundColor:"whitesmoke"

          }} className="btn badge-warning fa fa-times" onClick={()=>SetOpen()}></button>
       </div>
    <div className="col-sm-12 col-sm-offset-4 frame">
            <ul>


            <li style={{width:"100%"}}>
                        <div class="msj macro">
                        <div class="avatar"><img class="img-circle" style={{width:"100%"}}  src={"https://media.istockphoto.com/photos/elephants-fear-of-mice-picture-id155439372?b=1&k=20&m=155439372&s=170667a&w=0&h=4BMK9d2lxIvBFNRDx-T_JproEaxG2O1P38XudI2Wfkg="} /></div>
                            <div class="text text-l">
                                <p>
                                Neque porro quisquam est qui dolorem ipsum quia dolor sit amet, consectetur, adipisci velit..."
                                "There is no one who loves pain itself, who seeks after it and wants to have it, simply because it is pain... 
                                </p>
                                <p><small>'+date+'</small></p>
                            </div>
                        </div>
                    </li>


                    <li style={{width:"100%"}}>
                        <div class="msj-rta macro">
                            <div class="text text-r">
                                <p>
                                Neque porro quisquam est qui dolorem ipsum quia dolor sit amet, consectetur, adipisci velit..."
                                "There is no one who loves pain itself, who seeks after it and wants to have it, simply because it is pain...
                                </p>
                                <p><small>'+date+'</small></p>
                            </div>
                        <div class="avatar" style={{padding:"0px 0px 0px 10px !important"}}><img class="img-circle" style={{width:"100%"}} src={"https://media.istockphoto.com/photos/side-view-longbeard-business-man-portrait-picture-id1074638206?b=1&k=20&m=1074638206&s=170667a&w=0&h=Plsz2gRhhTANAmsCHpjINnXU_GtaSAyFv7PlNTAe94Q="} /></div>                                
                  </div>
                  </li>   
            </ul>
            <div style={{marginBottom:"5px"}}>
                <div className="msj-rta macro">                        


                        <textarea style={{width:"100%",outline:"none"}} className="mytext" placeholder="Type a message"/>


                </div>
                <div style={{padding:"10px",backgroundColor:"#cff",
                   borderRadius:"10px",
                   marginLeft:"1px",
                   cursor:"pointer"
                 }}>
                    <span className="fa fa-share"></span>
                </div>                
            </div>

        </div>  


        </div>

          </>
}

export default ChatBox;
