import axios from 'axios';
import download from 'js-file-download';

import { Tokens, UpdateToken } from "../authThenticateToken";
import Axios from "../config";
import Urls from '../urls';


const body =()=>{
     return{  
        "x-access-key": Tokens().accessToken,
        "x-access-token": Tokens().refreshToken
      }
    }

  async function sendUpload(endPoint,formData){
    try {


      const result = await Axios.post(endPoint,formData);

      if(result.data == false)
      {
          const data = await UpdateToken(Tokens().refreshToken);
          if(data == true)
          {
            const Result = await Axios.post(endPoint,formData);
            return Result.data;
          }
      }
      else{
          return result.data;
      }
    } catch (error) {
       //console.log(error)
    }
    }
  /*  unblockAdmin
    blockAdmin
    user_id*/
 const ApiRequest={
       DashBoardData:async()=>{
        try {
            const endPoint =`dashboard`;

            const result = await Axios.post(endPoint,body());
            if(result.data == false)
            {
                const data = await UpdateToken(Tokens().refreshToken);
                if(data == true)
                {
                  const Result = await Axios.post(endPoint,body());
                  return Result;
                }
            }
            else{
                return result;
            }
     } catch (error) {
         //console.log(error)
     }
       },
       getOrdersInProgress:async ()=>{
        try {
