import axios from 'axios';
import download from 'downloadjs';

import { Tokens, UpdateToken } from "../authThenticateToken";
import Axios from "../config";


const body =()=>{
     return{  
        "x-access-key": Tokens().accessToken,
        "x-access-token": Tokens().refreshToken
      }
    }

  async function sendUpload(endPoint,formData){
    try {


      const result = await Axios.post(endPoint,formData);

      if(result.data == false)
      {
          const data = await UpdateToken(Tokens().refreshToken);
          if(data == true)
          {
            const Result = await Axios.post(endPoint,formData);
            return Result.data;
          }
      }
      else{
          return result.data;
      }
    } catch (error) {
        console.log(error);
    }
    }
  /*  unblockAdmin
    blockAdmin
    user_id*/
 const ApiRequest={
       DashBoardData:async()=>{
        try {
            const endPoint =`dashboard`;

            const result = await Axios.post(endPoint,body());
            if(result.data == false)
            {
                const data = await UpdateToken(Tokens().refreshToken);
                if(data == true)
                {
                  const Result = await Axios.post(endPoint,body());
                  return Result;
                }
            }
            else{
                return result;
            }
     } catch (error) {
          console.log(error);
     }
       },
       getOrdersInProgress:async ()=>{
        try {
            const endPoint = `in-progress`;
            const result = await Axios.post(endPoint,body());

            if(result.data == false){
             const data = await UpdateToken(Tokens().refreshToken);
             if(data == true){
               const Result = await Axios.post(endPoint,body());
                 console.log(Result)
               return Result;
             }
            }else{

                return result;
            }
      } catch (error) {
            return[];
           console.log(error);
      }
       },

       RegisterWriter:async (email,username,password,ConfirmPassword,country,address,phone,category)=>{

        var data = 'email='+email+'&username='+username+'&password='+password+'&country='+country+'&address='+address+'&phone='+phone+'&category='+category;

        try {
          const endPoint =`register/writer?`+data;

          const result = await Axios.post(endPoint,body());
          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }

       },
       getCategories:async ()=>{
        try {
          const endPoint =`category`;

          const result = await Axios.post(endPoint,body());
          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }
      },
      getWriters:async()=>{
        //writers
        try {
          const endPoint =`writers`//+data;

          const result = await Axios.post(endPoint,body());

          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }
      },


      BlockAdmin:async(id)=>{
        try {
          const endPoint =`blockAdmin?`+"user_id="+id//+data;

          const result = await Axios.post(endPoint,body());
          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }
      },
      unblockAdmin:async(id)=>{
        try {
          const endPoint =`unblockAdmin?`+"user_id="+id//+data;

          const result = await Axios.post(endPoint,body());
          console.log(result)
          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }
      },












      BlockUser:async(id)=>{
        try {
          const endPoint =`block?`+"user_id="+id//+data;

          const result = await Axios.post(endPoint,body());
          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }
      },
      unblockUser:async(id)=>{
        try {
          const endPoint =`unblock?`+"user_id="+id//+data;

          const result = await Axios.post(endPoint,body());
          console.log(result)
          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }
      },

      getUserDetails:async (id)=>{
       // writer/details/2

       try {
        const endPoint =`writer/details/${id}`//+data;

        const result = await Axios.post(endPoint,body());

        if(result.data == false)
        {
            const data = await UpdateToken(Tokens().refreshToken);
            if(data == true)
            {
              const Result = await Axios.post(endPoint,body());
              return Result;
            }
        }
        else{
            return result.data;
        }
     }catch(e){
      console.log(e);
     }


      },
      getUnAssignedTask:async ()=>{

        try {
          const endPoint =`unAssignedTask`//+data;

          const result = await Axios.post(endPoint,body());

          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){
        console.log(e);
       }



      },

      asignTaskToUser:async(user_id,task_id)=>{

        try {
          const endPoint =`AssignTask/${user_id}/${task_id}`;//user_id,task_id

          const result = await Axios.post(endPoint,body());

          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){

        console.log(e);
        return [];
       }

      },

      postAllowedTaskNumber:async (number)=>{
        //allowedTask
        try {
          const endPoint =`allowedTask/${number}`;//user_id,task_id

          const result = await Axios.post(endPoint,body());

          if(result.data == false)
          {
              const data = await UpdateToken(Tokens().refreshToken);
              if(data == true)
              {
                const Result = await Axios.post(endPoint,body());
                return Result;
              }
          }
          else{
              return result;
          }
       }catch(e){

        console.log(e);
        return [];
       }
      },

      postSequence:async (sequence)=>{
