import { useState } from 'react';
import ModalCard from 'src/views/modal/Modal';
import TableCard from '../../../table/TableCard';
import NewWriter from './newWriter';
const Writers = ()=>{

    const notify = () => toast("Wow so easy!");

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const CreateNewWriter = ()=>{

    }

    const Buttons =(id)=>{
     return <> 
          <button style={{color:"blue"}} className="fa fa-edit btn"></button> 
          <button style={{color:'red'}} className="fa fa-trash btn"></button> 
            <div class="custom-control custom-switch">
          <input type="checkbox" class="custom-control-input" id={"customSwitch"+id}/>
           <label class="custom-control-label" for={"customSwitch"+id}>Off/On</label>
          </div>
      </>
        }

    const columns = [
        {
          dataField: "#writerId",
          text: "WriterId",

        },
        {
          dataField: "image",
          text: "Profile",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "writercategory",
            text: "writer Category",


        },
        {
            dataField: "Action",
            text: "Action",

          }
      ];

      const products = [
           { 
               "#writerId":"#9988",
                image:"image",
                writerName:"Airo",
                email:"airotony8@gmail.com",
                status:<span className="badge badge-primary">cancelled</span>,
                writercategory:"Litrature",
                Action:Buttons(1)
         },
         { 
            "#writerId":"#9988",
             image:"image",
             writerName:"Airo",
             email:"airotony8@gmail.com",
             status:<span className="badge badge-primary">cancelled</span>,
             writercategory:"Litrature",
             Action:Buttons(2)
      },
      { 
        "#writerId":"#9988",
         image:"image",
         writerName:"Airo",
         email:"airotony8@gmail.com",
         status:<span className="badge badge-primary">cancelled</span>,
         writercategory:"Litrature",
         Action:Buttons(3)
  },

      ];

    return <>
    <ModalCard content={<NewWriter/>} Show={show} Close={handleClose} Save={CreateNewWriter} ButtonName={"Create New Writer"}/>
        <div style={{height:"60px",display:"flex",flexDirection:"row",justifyContent:"space-between"}} className="card">
            <h4 style={{marginTop:"10px",marginLeft:"20px"}}><i>CREATE NEW USER</i></h4>
             <button onClick={()=>handleShow()} style={{width:"40px",height:"40px",marginRight:"10px",marginTop:"7px"}} className="btn btn-primary fa fa-plus"></button>
        </div>
      <div className="container card">
       <TableCard products={products} columns={columns} title={"Writers"}/>
       </div>
       <button onClick={notify}>Notify!</button>
       <ToastContainer />
    </>
}

export default Writers;
