import CategoryEdit from "./editCategory";

const EditData=({initialSelected,selected,setSelected,options,setInitialSelected})=>{
    function Map(e){
        var elem = document.getElementById(e);
        console.log(elem)
        elem.parentNode.removeChild(elem);
        //e.parentNode.removeChild(e);
    }
        return<>  
              <div style={{display:"flex",flexDirection:"row",marginBottom:"6px"}}>
                  {
                      initialSelected.map((item,index)=>{
                        return <button id={"btn_"+item.value}  onClick={()=> {
                            const data = initialSelected.splice(index,1)
                            //console.log(data)
                            for (let i = 0; i < data.length; i++) {
                                const element = data[i];
                                setInitialSelected(element);
                            }


                          //  Map("btn_"+item.value);


                          //console.log(initialSelected)


                      }} style={{color:'red'}} className="fa fa-trash btn">{item.label}</button>
                    })
                  }


             </div>

            <CategoryEdit selected={selected} setSelected={setSelected} data={options}/>
             </>
}

export default EditData;
