//import { cilSortNumericDown } from "@coreui/icons";
import { useState } from "react";
import ModalCard from "src/views/modal/Modal";
import TableCard from "src/views/table/TableCard";
import swal from "sweetalert";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

const NewTaskSubmited = ()=>{

  const [Show,setShow]=useState(false);
  const [showRedoneModal,setRedoneModal]= useState(false);

  const [task,setTask]=useState([]);

  const OpenModal =(id,invoice)=>{
      setShow(true)

      setTask({taskid:id,invoice:invoice});
  }

  const Close=()=>{
      setShow(false);
      setTask([]);
  }

  const SubmitRevisionDetails=()=>{
    swal(`REVISION REQUEST`, "Sent Succesfully", "success")

        setShow(false);
        setRedoneModal(false);
        setTask([]);

  }

  const Content =()=>{
      return <>
 <ul className="list-group">
  <li className="list-group-item d-flex justify-content-between align-items-center">
    Cras justo odio
    <span style={{cursor:"pointer"}} className="badge badge-primary badge-pill">Download</span>
  </li>
  <li  className="list-group-item d-flex justify-content-between align-items-center">
    Dapibus ac facilisis in
    <span style={{cursor:"pointer"}} className="badge badge-primary badge-pill">Download</span>
  </li>
  <li className="list-group-item d-flex justify-content-between align-items-center">
    Morbi leo risus
    <span  style={{cursor:"pointer"}} className="badge badge-primary badge-pill">Download</span>
  </li>
</ul>
       </>
  }

  const RevisionContenT=()=>{
      return <>
               {<h6>REVISION REQUEST FOR ORDER {task['taskid']}</h6>}
               <br/>
                 <label>Revision Details</label>
               <CKEditor

       editor={ ClassicEditor }
      // data={message}

       onReady={ editor => {
           // You can store the "editor" and use when it is needed.
           //console.log( 'Editor is ready to use!', editor );
           editor.editing.view.change((writer) => {
            writer.setStyle(
                "height",
                "300px",
                editor.editing.view.document.getRoot()
            );
            });
       } }
       onChange={ ( event, editor ) => {
           const data = editor.getData();

           //setMessage(data);
       } }

   />
            </>
  }

  const RequestRedone=()=>{
      setShow(false);
      setRedoneModal(true);

  }

    const OpenMoreModal=(id,invoice)=>{
      swal(`INVOICE +${invoice}`,{
            buttons:{
                file:{
                    text:"SUBMITED FILES",
                    value:"file",
                    className:"btn btn-outline-info"
                },

                details:{
                    text:"ORDER DETAILS",
                    value:"details",
                    className:"btn btn-info"
                },
            }
      })
      .then((value)=>{
          switch (value) {
              case "file":
                  OpenModal(id,invoice);
                  break;

                    case "details":

                        break;  

              default:
                  break;
          }
      })
    }
    const Buttons=(id,invoice)=>{
        return <button onClick={()=>OpenMoreModal(id,invoice)} className="btn btn-primary">More</button>
     }

   const AcceptOrder=()=>{
       swal(`You Are About To Mark Order ${task['taskid']} As Complete`,{
           buttons:{
               cancel:"Cancel !",
               complete:{
                   text:"COMPLETED?",
                    value:"complete",
                    className:"btn btn-success"
               }
           }
       })
       .then((value)=>{
           switch (value) {
               case 'complete':
                setShow(false);
                setRedoneModal(true);
                     swal("ORDER COMPLETED","succesfully","success");
                   break;

               default:
                   swal("Process Cancelled ","succesfully","success");
                   break;
           }
       })
   }  

     const Btns=()=>{
        return <> 
                  <button onClick={()=>RequestRedone()} className="btn btn-info">REQUEST REDONE</button>
                  <button onClick={()=>AcceptOrder()}  className="btn btn-success">ACCEPT ORDER</button>

              </>
     }

    const columns=[
        {
            dataField: "#taskId",
            text: "#taskId",

          },
          {
            dataField: "username",
            text: "writername",

          },
          {
            dataField: "email",
            text: "writer email",

          },
          {
            dataField: "taskTitle",
            text: "Task Title",

          },
          {
            dataField: "sdate",
            text: "submited Date",

          },
          {
            dataField: "edate",
            text: "DeadLine",

          },
          {
            dataField: "action",
            text: "Actions",

          },
    ]
    const Products=[
                {
                    "#taskId":"98272282",
                    username:"airo",
                    email:"airotony8@gmail.com",
                    taskTitle:"ggdttsssjjsbsbs",
                    sdate:"2020-8-9",
                    edate:"2020-7-8",
                    action:Buttons(5,"#55277772")
                }
    ]

    return <>
           <ModalCard
            Show={Show} Close={Close}
            showBtn={false}
            Buttons={Btns}
            content={Content()}
             />

             <ModalCard
              Show={showRedoneModal}
               ButtonName={"SEND REVISION REQUEST"}
               content={RevisionContenT()}
               Save={SubmitRevisionDetails}

             />
          <TableCard products={Products} columns={columns} title={"NEW SUBMITED TASK"}/>
     </>


}

export default NewTaskSubmited;
