import { useState } from "react";
import CategoryEdit from "src/views/base/Users/writers/editCategory";
import Profile from "src/views/base/Users/writers/profile/profile";
import TableCard from "src/views/table/TableCard";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import swal from 'sweetalert';

const ReasignUser = ()=>{
       const [edit,setEdit] = useState(false);
       const [category,setCategory] = useState([]);
       const [userId,setUserId]=useState('');

       const notify = (data) => toast(data);

       const [initialSelected,setInititialSelected] = useState([
        { label: "Short Stories.",value:"short"},
        { label: "Technically",value:"technical"},
        { label: "Novellas",value:"novels"},
        { label: "Scripts and Screenplays",value:"scripts"},
        { label: "True Crime. ...",value:"true"},
        { label: " Auto/Biography.",value:"auto"},
       ]);
       const options = [
        { label: "Short Stories.",value:"short"},
        { label: "Technically",value:"technical"},
        { label: "Novellas",value:"novels"},
        { label: "Scripts and Screenplays",value:"scripts"},
        { label: "True Crime. ...",value:"true"},
        { label: " Auto/Biography.",value:"auto"},

        ];

        const BlockUserPermanet=(id,username)=>{
            swal("You Are About To Permanently Block "+username+"!?", {
                buttons: {
                  cancel: "Dont Delete!",
                  delete: {
                    text: "Delete User",
                    value: "delete",
                  },

                },
              })
              .then((value) => {
                switch (value) {



                  case "delete":
                    swal(`${username}`, "Deleted Succesfully", "success");
                    break;

                  default:
                    swal("Process Canceled Succesfully!");
                }
              });
        }

        const DisableUser =(id,event)=>{
            if(event){
                //activate account
                notify('activating account for '+id)

            }else{
                //deactivate account
                notify('Deactivating account for '+id)
            }

        }
        const EditUser=(id)=>{
            setUserId(id);
            setEdit(true)
       }

        const Back=()=>{
            setUserId('')
            setEdit(false);
          }

    const ViewUser=(id)=>{

    }

    const SingleTask=(id)=>{

    }

    const columns=[
        {
            dataField: "username",
            text: "username",

          },
          {
            dataField: "email",
            text: "email",

          },
          {
            dataField: "action",
            text: "Action",

          },
    ]
    const products=[
        {
            username: "Wilson",
            email: "wilson@gmail.com",
            action:<>
                     <button style={{marginRight:"3px"}} className="btn btn-success fa fa-check"></button>
                     <button onClick={()=>EditUser(1)} className="btn btn-primary fa fa-user"></button>
                   </>

          },
    ]
    return  <> {!edit && 

     <div className="container">
    <div className="main-body">




          <div className="row gutters-sm">

            <div style={{marginTop:"20px"}} className="col-md-12">

              <div className="row gutters-sm">
                <div className="col-sm-6 mb-3">
                  <div className="card h-100">
                    <div className="card-body">
                      <h6 className="d-flex align-items-center mb-3"><i className="material-icons text-info mr-2">Orders </i>Details</h6>
                      <div className="form-group">
                           <label for="inputAddress2">Order Dtails</label>
                           <div class="container">

                            <div class="row">
                                <div class="col">
                                     Order Title
                                </div>
                              <div class="col">
                                  Lorem Ipsum
                               </div>
                                 </div>
                            <div class="row">
                            <div class="col">
                                status
                              </div>
                             <div class="col">
                               <span className="badge badge-warning">in-progress</span>
                           </div>
                          <div class="col">
                            2018-9-10
                             </div>
                         </div>
                         <br/>
                         <div class="row">
                            <div class="col card">
                                <label>Description</label>
                                <p>kkkkkkk</p>
                              </div>

                         </div>


                       </div>

                         </div>

                    </div>
                  </div>
                </div>

                <div className="col-sm-6 mb-3">
                  <div className="card h-100">
                    <div className="card-body" style={{overflowY:"auto"}}>
                      <h6 className="d-flex align-items-center mb-3"><i className="material-icons text-info mr-2">System</i>Billing Sequence</h6>
                      <div className="form-group">
                           <label for="inputAddress2">Filters Users &nbsp;<span className="badge badge-warning">1 week</span></label>
                         <select className="form-control">
                             <option selected>
                                  users under assigned categorie
                             </option>
                             <option>
                                  All
                             </option>

                         </select>
                         </div>
                     <TableCard title={"AVAILABLE USERS"} products={products} columns={columns}/>

                    </div>

                  </div>
                </div>


              </div>



            </div>
          </div>

        </div>
    </div>}

    {edit && <Profile
       category={<CategoryEdit data={options} selected={category}  setSelected={setCategory}/>}
       existingCategories={initialSelected}
       back={Back}
       DisableUser={DisableUser}
       id={userId}
    />}
    <ToastContainer/>
    </>
}

export default ReasignUser;
