import { useState } from "react";
import { toast, ToastContainer } from "react-toastify";
import ModalCard from "src/views/modal/Modal";
import TableCard from "src/views/table/TableCard";
import CreateAdmin from "./CreateAdmins";
import RegisteredAdminProfile from "./registeredAdmins/registeredAdminsProfile";

import 'react-toastify/dist/ReactToastify.css';
import swal from 'sweetalert';




const Administrators=()=>{
    const [edit,setEdit] = useState(false);

    const notify = (data) => toast(data);
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [ConfirmPassword,setConfirmPassword] = useState('');
    const [country,setCountry] = useState('');
    const [address,setAdress] = useState('');
    const [phone,setPhone] = useState('');
    const [category,setCategory] = useState([]);
    const [username,setUsername] = useState('');
    const [userId,setUserId]=useState('');

    const handleClose = () => {setShow(false);setEdit(false)};
    const handleShow = () => setShow(true);
    const [show, setShow] = useState(false);
    const [isShowLoader,setShowLoader] = useState(false);

    const Save = ()=>{
        setShowLoader(true);
    }

    const Category=()=>{
        return <>
              <select className="form-control">
                  <option value="O" selected>chose</option>
                  <option>Super Admin</option>
                  <option>Manager</option>
              </select>
            </>
    }

    const ProfileImage=image=>{

      return <img 
              style={{
                width:"30px",
                height:"30px"
              }}
          src={image} alt="profile"/>
    }


  const EditUser=(id)=>{
       setUserId(id);
      setEdit(true)
  }
  const Back=()=>{
    setUserId('')
    setEdit(false);
  }

  const DisableUser =(id,event)=>{
      if(event){
          //activate account
          notify('activating account for '+id)

      }else{
          //deactivate account
          notify('Deactivating account for '+id)
      }

  }

    const Buttons =(id,username)=>{
      return <> 
           <button style={{color:"blue"}} onClick={()=>EditUser(id)} className="fa fa-edit btn"></button> 

             <div class="custom-control custom-switch">
           <input type="checkbox"  onChange={(e)=>DisableUser(id,e.target.checked)} class="custom-control-input" id={"customSwitch"+id}/>
            <label class="custom-control-label" for={"customSwitch"+id}>Off/On</label>
           </div>
       </>
         }


    const columns = [
        {
          dataField: "#Id",
          text: "Id",

        },
        {
          dataField: "image",
          text: "Profile",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "level",
            text: "Administartor Type",


        },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
         {
           "#Id":"33444",
            image:ProfileImage('https://media.istockphoto.com/photos/senior-man-remembering-picture-id1166588777?b=1&k=20&m=1166588777&s=170667a&w=0&h=lNpNLSTkPOAvvo4TwEbUQa_p0IjFUQ6K5bjodzSzJok='),
            writerName:"Airo",
            email:"airotony8@",
            status:"status",
            level:"level",
            Action:Buttons(5,"airo")
         }
      ]
 return <>  {!edit && <> <div style={{height:"60px",display:"flex",flexDirection:"row",justifyContent:"space-between"}} className="card">
    <h4 style={{marginTop:"10px",marginLeft:"20px"}}><i>CREATE NEW ADMINISTRATOR</i></h4>
     <button onClick={()=>handleShow()} style={{width:"40px",height:"40px",marginRight:"10px",marginTop:"7px"}} className="btn btn-primary fa fa-plus"></button>
</div>
<div className="container card">
<TableCard products={products} columns={columns} title={"Admin-Strators"}/>
</div>


 <ModalCard 
  content={<CreateAdmin
    isShowLoader={isShowLoader}
     email={email}
     setEmail={setEmail}
     password={password}
     setPassword={setPassword}
     ConfirmPassword={ConfirmPassword}
     setConfirmPassword={setConfirmPassword}
     country={country}
     setCountry={setCountry}
     address={address}
     setAdress={setAdress}
     phone={phone}
     setPhone={setPhone}
     category={<Category/>}
     setCategory={setCategory}
     username={username}
     setUsername={setUsername}/>}

     ButtonName={"CREATE NEW ADMIN"}
     Save={Save}
     isShowLoader={isShowLoader}
 Show={show} Close={handleClose}/>
</>}

{edit && <RegisteredAdminProfile back={Back}/>}

<ToastContainer />
</>
}

export default Administrators;
