import { useEffect, useState } from "react";
import TableCard from "src/views/table/TableCard";
import swal from "sweetalert";
import ReasignUser from "./re-assign/reasign";
import SingleTask from '../../TaskDetails/SingleTaskDetails';

const AssignedTask = ()=>{

  const [reaAsign,setAssign]=useState(false);
  const [bck,setBck] = useState(true);
  const [taskDetails,setTaskDetails] = useState(false);
  const [taskId,setTaskId]=useState('');

  useEffect(()=>{
      setBck(true);
  },[])

  const Reasign=(taskId,taskName)=>{
     swal(`Are you sure to reasign`,`${taskName}`,"warning",{
       buttons:{
         cancel:"cancel !",
         proceed:{
           text:"PROCEED",
           value:"proceed"
         }
       }
     })
     .then((value)=>{
         switch (value) {
           case 'proceed':
              setAssign(true);
              setBck(false)
             break;

           default:
             break;
         }
     })
  }
  const back=()=>{
    setAssign(false);
  }

  const CloseTask=()=>{
    setTaskDetails(false);
  }
  const TaskDetails=(taskId)=>{
    setTaskId(taskId);
     setTaskDetails(true);
  }

  const WriterDeatails=(writerId)=>{

  }
    const columns = [
        {
          dataField: "#taskId",
          text: "#taskId",

        },
        {
          dataField: "title",
          text: "Title",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Writer Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "category",
            text: "Category",


        },
        {

            dataField: "date",
            text: "Assigned Date",


        },
        {

            dataField: "accepted",
            text: "Accepted-Date",


        },
        {

            dataField: "deadline",
            text: "Deadline",


        },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
        {
          "#taskId":"7888888",
          title:"Lorem ipsume",
          writerName:"John ",
          email:"airotony8@gmail.com",
          status:"Revision",
          category:"Lorem",
          date:"2018-9-10",
          accepted:"2019-10-10",
          deadline:"2019-10-10",
          Action:<>
          <div style={{display:"flex",flexDirection:"column"}}>
          <button onClick={()=>Reasign(1,'LOREM IPSUM')} style={{marginBottom:"4px",width:'40px'}} className="btn btn-warning fa fa-repeat"></button>
          <button onClick={()=>TaskDetails(1)} style={{marginBottom:"4px",width:'40px'}} className="btn btn-info">Task</button>

          </div>

            </>

        }
      ]
    return <>
            {!reaAsign && !taskDetails &&  <TableCard 
             products={products}
             columns={columns}
              title={"ASSIGNED TASK"}
             />}

             {reaAsign && !taskDetails && <> <br/>
             {bck && <><div style={{height:"60px",display:"flex",flexDirection:"row",justifyContent:"space-between"}} className="card">
            <h4 style={{marginTop:"10px",marginLeft:"20px"}}><i>Go Back</i></h4>
             <button  onClick={()=>back()}  style={{width:"40px",height:"40px",marginRight:"10px",marginTop:"7px",borderRadius:"20px"}} className="btn btn-primary fa fa-history"></button>
               </div></>}
             <ReasignUser buttonName={"RE-ASSIGN"} setBack={setBck}/></>}

             {!reaAsign && taskDetails &&<SingleTask id={taskId} closeTask={CloseTask} isAccept={false}/>}
          </>

}

export default AssignedTask;
