import { useState } from "react";

import ChatBox from "../ChatBox/ChatBox";
import TableCard from "../table/TableCard";

const Support = ()=>{

   const [open,setOpen]=useState(false);


   const Open=()=>{
      setOpen(!open);
   }
   const ProfileImage=image=>{

      return <img 
              style={{
                width:"30px",
                height:"30px"
              }}
          src={image} alt="profile"/>
    }

    const Buttons =(id,username)=>{
      return <> 
           <button style={{color:"blue"}} onClick={()=>Open()}  className="fa fa-reply btn"></button> 


       </>
         }



   const columns = [
        {
          dataField: "#Id",
          text: "TicketId",

        },
        {
          dataField: "image",
          text: "Profile",

          sort: true
        },

        {
          dataField: "email",
          text: "email",

        },

        {

            dataField: "Odate",
            text: "OpenDate",


        },
        {
            dataField: "Action",
            text: "Action",

          }
      ];
      const products=[
         {
           "#Id":"33444",
            image:ProfileImage('https://media.istockphoto.com/photos/senior-man-remembering-picture-id1166588777?b=1&k=20&m=1166588777&s=170667a&w=0&h=lNpNLSTkPOAvvo4TwEbUQa_p0IjFUQ6K5bjodzSzJok='),

            email:"airotony8@",

            Odate:"2020-17-9",
            Action:Buttons(5,"airo")
         }
      ]
 return <>  
<div className="container card">
<TableCard products={products} columns={columns} title={"SUPPORT TICKETS"}/>
</div>

       <ChatBox 
         open={open}
         SetOpen={Open}
        />
   </>;
}

export default Support;
