import { useState } from 'react';
import ModalCard from 'src/views/modal/Modal';
import TableCard from '../../../table/TableCard';
import NewWriter from './newWriter';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import swal from 'sweetalert';
import CategoryEdit from './editCategory';


const Writers = ()=>{

    const notify = (data) => toast(data);

    const [show, setShow] = useState(false);
    const [isShowLoader,setShowLoader] = useState(false);

    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [ConfirmPassword,setConfirmPassword] = useState('');
    const [country,setCountry] = useState('');
    const [address,setAdress] = useState('');
    const [phone,setPhone] = useState('');
    const [category,setCategory] = useState('');
    const [username,setUsername] = useState('');


     const [initialSelected,setInititialSelected] = useState([{label: "Grapes ðŸ‡", value: "grapes" }]);
    const [selected, setSelecteds] = useState([]);
    const options = [
        { label: "Grapes ðŸ‡", value: "grapes" },
        { label: "Mango ðŸ¥­", value: "mango" },
        { label: "Strawberry ðŸ“", value: "strawberry", disabled: true },
      ];

      const setSelected=data=>{
           setSelecteds(data)
      }


    const [edit,setEdit] = useState(false);

    const handleClose = () => {setShow(false);setEdit(false)};
    const handleShow = () => setShow(true);

    const BlockUserPermanet=(id,username)=>{
        swal("You Are About To Permanently Block "+username+"!?", {
            buttons: {
              cancel: "Dont Delete!",
              delete: {
                text: "Delete User",
                value: "delete",
              },

            },
          })
          .then((value) => {
            switch (value) {



              case "delete":
                swal(`${username}`, "Deleted Succesfully", "success");
                break;

              default:
                swal("Process Canceled Succesfully!");
            }
          });
    }

    const DisableUser =(id,event)=>{
        if(event){
            //activate account
            notify('activating account for '+id)

        }else{
            //deactivate account
            notify('Deactivating account for '+id)
        }

    }

    const EditUser=(id)=>{
         setEdit(true)
    }

    const CreateNewWriter = ()=>{

        setShowLoader(true);
       notify("Creating new User");
       setTimeout(() => {
             setShowLoader(false);
             setEmail('')
       }, 2000);
    }

    const EditData=()=>{
            return<>  
                  <div style={{display:"flex",flexDirection:"row",marginBottom:"6px"}}>
                      {initialSelected.map((item)=>{
                          return <button style={{color:'red'}} className="fa fa-trash btn">{item.label}</button>
                      })}


                 </div>
                <CategoryEdit selected={selected} setSelected={setSelected} data={options}/>
                 </>
    }

    const Buttons =(id,username)=>{
     return <> 
          <button style={{color:"blue"}} onClick={()=>EditUser(id)} className="fa fa-edit btn"></button> 
          <button style={{color:'red'}} onClick={()=>BlockUserPermanet(id,username)} className="fa fa-trash btn"></button> 
            <div class="custom-control custom-switch">
          <input type="checkbox"  onChange={(e)=>DisableUser(id,e.target.checked)} class="custom-control-input" id={"customSwitch"+id}/>
           <label class="custom-control-label" for={"customSwitch"+id}>Off/On</label>
          </div>
      </>
        }

    const columns = [
        {
          dataField: "#writerId",
          text: "WriterId",

        },
        {
          dataField: "image",
          text: "Profile",

          sort: true
        },
        {
          dataField: "writerName",
          text: "Username",

        },
        {
          dataField: "email",
          text: "email",

        },
        {

            dataField: "status",
            text: "status",


        },
        {

            dataField: "writercategory",
            text: "writer Category",


        },
        {
            dataField: "Action",
            text: "Action",

          }
      ];

      const products = [
           { 
               "#writerId":"#9988",
                image:"image",
                writerName:"Airo",
                email:"airotony8@gmail.com",
                status:<span className="badge badge-primary">cancelled</span>,
                writercategory:"Litrature",
                Action:Buttons(1,"Linet")
         },
         { 
            "#writerId":"#9988",
             image:"image",
             writerName:"Airo",
             email:"airotony8@gmail.com",
             status:<span className="badge badge-primary">cancelled</span>,
             writercategory:"Litrature",
             Action:Buttons(2,"Brayo")
      },
      { 
        "#writerId":"#9988",
         image:"image",
         writerName:"Airo",
         email:"airotony8@gmail.com",
         status:<span className="badge badge-primary">cancelled</span>,
         writercategory:"Litrature",
         Action:Buttons(3,"Kevo")
  },

      ];

    return <>
   <ModalCard
     content={
         EditData()
     }
    Show={edit}
    Close={handleClose}
    ButtonName={"Edit Writer Category"}
   />
    <ModalCard 
       content={<NewWriter 
        isShowLoader={isShowLoader}
         email={email}
         setEmail={setEmail}
         password={password}
         setPassword={setPassword}
         ConfirmPassword={ConfirmPassword}
         setConfirmPassword={setConfirmPassword}
         country={country}
         setCountry={setCountry}
         address={address}
         setAdress={setAdress}
         phone={phone}
         setPhone={setPhone}
         category={category}
         setCategory={setCategory}
         username={username}
         setUsername={setUsername}

        />}
        Show={show}
        Close={handleClose}
       Save={CreateNewWriter} ButtonName={"Create New Writer"}
      />
        <div style={{height:"60px",display:"flex",flexDirection:"row",justifyContent:"space-between"}} className="card">
            <h4 style={{marginTop:"10px",marginLeft:"20px"}}><i>CREATE NEW USER</i></h4>
             <button onClick={()=>handleShow()} style={{width:"40px",height:"40px",marginRight:"10px",marginTop:"7px"}} className="btn btn-primary fa fa-plus"></button>
        </div>
      <div className="container card">
       <TableCard products={products} columns={columns} title={"Writers"}/>
       </div>

       <ToastContainer />
    </>
}

export default Writers;
