import React, { useEffect, useState } from 'react'
import inactivityTime from 'src/idleTimeDetection'

import {
  TheContent,
  TheSidebar,
  TheFooter,
  TheHeader
} from './index'

const TheLayout = ({Uname,Pfile,email}) => {
   const [notificationCount,setNotificationCount] = useState('');

   const handleNotifications=(notifications)=>{
           setNotificationCount([{
             count:notifications.count,
             messages:notifications.messages
           }])
   }

useEffect(()=>{

  inactivityTime();
},[])
  return (
    <div className="c-app c-default-layout">
      <TheSidebar username={Uname}/>
      <div className="c-wrapper">
        <TheHeader notificationCount={notificationCount[0].count} profile={Pfile}/>
        <div className="c-body">
        <TheContent notificationCount={handleNotifications} email={email} name={Uname} profile={Pfile}/>
        </div>
        <TheFooter/>
      </div>
    </div>
  )
}

export default TheLayout
