import React, { useState } from 'react'
import {
  CBadge,
  CDropdown,
  CDropdownItem,
  CDropdownMenu,
  CDropdownToggle,
  CProgress
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import ModalCard from 'src/views/modal/Modal'


const TheHeaderDropdownTasks = () => {
      const [show,SetShow] = useState(false);
      const [isShowLoader,setIshowLoader]= useState(false);

      const Open=()=>{
         SetShow(true);
      }
      const close=()=>{
        SetShow(false);
      }
      const Save=()=>{
         setIshowLoader(true);

      }
  return (
    <>
    <CDropdown
      inNav
      className="c-header-nav-item mx-2"

    >
      <CDropdownToggle onClick={()=>open()} className="c-header-nav-link" caret={false}>
        <CIcon name="cil-list" />

      </CDropdownToggle>

    </CDropdown>
    <ModalCard 
     Close={close}
     Save={Save}
     ButtonName={"Add Payment Method"}
    Show={show}
    isShowLoader={isShowLoader}
    />

    </>
  )
}

export default TheHeaderDropdownTasks
