import React, { useState } from "react";
import Invoice from "../../Invoice/invoice";
import TableCard from "../../Table/tableCard";

const PaymentHistory=()=>{

    const [isShowInvoice,setShowInvoice]=useState(false);
    const [InvoiceId,setInvoiceId] = useState('');
    const ViewItemDetails =(id)=>{
       //alert(id);
       setInvoiceId(id);
       isShowInvoice(true);
    }

   const colums= React.useMemo(
       ()=>[
        {
            Header: 'Paid Invoices',
            columns: [
                {
                    Header: '#Invoice',
                    accessor: '#invoice',
                },
                {
                    Header: 'Writer Name',
                    accessor: 'writername',
                },
                {
                    Header: 'Date Paid',
                    accessor: 'datepaid',
                },


                {
                    Header: 'Action',
                    accessor: 'action',
                },

            ],
        },
       ]
   )


     const data=[
         {
            "#invoice": "committee-c15dw",
            "writername": "editor-ktsjo",
            "datepaid": "2021-07-7",
            "action": <button className="btn btn-info" onClick={()=>ViewItemDetails(5)}>Details</button>
         }
     ]
    return <React.Fragment>
        {!isShowInvoice &&<TableCard columns={colums} data={data}/>}
        {isShowInvoice && <Invoice/>}
    </React.Fragment>
}

export default PaymentHistory;
