import React from "react";
import SingeleTask from "../../NewTask/taskDetails/singleTaskDetails";
import TableCard from "../../Table/tableCard";


const OrdersBeingRedone =()=>{

    const [isSingleTaskOpen,setShowSingleTask] = useState(false);
    const [singleTaskId,setSingleTaskId] = useState();

    const handleShowSingleTask=(id)=>{

          setSingleTaskId(id);
          setShowSingleTask(true);
    }


    const handleCloseTask=()=>{
       setSingleTaskId('');
       setShowSingleTask(false);
    }
    //const ViewItemDetails =(id)=>{
    //    alert(id);
   // }

   const colums= React.useMemo(
       ()=>[
        {
            Header: 'REVISIONS',
            columns: [
                {
                    Header: '#order',
                    accessor: '#order',
                },
                {
                    Header: 'Order Name',
                    accessor: 'ordername',
                },
                {
                    Header: 'Title',
                    accessor: 'title',
                },
                {
                    Header: 'status',
                    accessor: 'status',
                },
                {
                    Header: 'Created on',
                    accessor: 'created',
                },
                {
                    Header: 'Deadline',
                    accessor: 'deadline',
                },
                {
                    Header: 'Service',
                    accessor: 'service',
                },
                {
                    Header: 'Pages',
                    accessor: 'page',
                },

                {
                    Header: 'Action',
                    accessor: 'action',
                },

            ],
        },
       ]
   )

     const data=[
         {
            "#order": "committee-c15dw",
            "ordername": "editor-ktsjo",
            "title": 3,
            "status": <span className="badge badge-danger">cancelled</span>,
            "created": '2021-07-7',
            "deadline": '2021-07-7',
            "service": "paraprhrasing",
            "page": "5",
            "action": <button className="btn btn-info" onClick={()=>handleShowSingleTask(5)}>Details</button>
         }
     ]
    return <React.Fragment>
            {!isSingleTaskOpen && <TableCard columns={colums} data={data}/>}
            {isSingleTaskOpen && <SingeleTask closeTask={handleCloseTask} id={singleTaskId}/>}
        </React.Fragment> 
}

export default OrdersBeingRedone;
