import React, { useState } from "react";
import SingeleTask from "../../NewTask/taskDetails/singleTaskDetails";
import TableCard from "../../Table/tableCard";

const OrdersInProgress =()=>{

    const [isSingleTaskOpen,setShowSingleTask] = useState(false);
    const [singleTaskId,setSingleTaskId] = useState();

    const handleShowSingleTask=(id)=>{

          setSingleTaskId(id);
          setShowSingleTask(true);
    }


    const handleCloseTask=()=>{
       setSingleTaskId('');
       setShowSingleTask(false);
    }

    const ViewItemDetails =(id)=>{
       // alert(id);
       handleShowSingleTask(id);
    }



const CancelOrder = (id)=>{
    alert(id);
}

const Buttons =(id)=>{
    return <React.Fragment>
        <button className="btn btn-danger" onClick={()=>CancelOrder(8)}>Submit Order</button>&nbsp;
         <button className="btn btn-danger" onClick={()=>CancelOrder(8)}>cancel Order</button>&nbsp;
         <button className="btn btn-info" onClick={()=>ViewItemDetails(8)}>Details</button>
    </React.Fragment>
}


const colums= React.useMemo(
   ()=>[
    {
        Header: 'Your Current Ongoing Orders',
        columns: [
            {
                Header: '#order',
                accessor: '#order',
            },
            {
                Header: 'Order Name',
                accessor: 'ordername',
            },
            {
                Header: 'Title',
                accessor: 'title',
            },
            {
                Header: 'status',
                accessor: 'status',
            },
            {
                Header: 'Created on',
                accessor: 'created',
            },
            {
                Header: 'Deadline',
                accessor: 'deadline',
            },
            {
                Header: 'Service',
                accessor: 'service',
            },
            {
                Header: 'Pages',
                accessor: 'page',
            },

            {
                Header: 'Action',
                accessor: 'action',

            },

        ],
    },
   ]
)

 const data=[
     {
        "#order": "committee-c15dw",
        "ordername": "editor-ktsjo",
        "title": 3,
        "status": <span className="badge badge-danger">cancelled</span>,
        "created": '2021-07-7',
        "deadline": '2021-07-7',
        "service": "paraprhrasing",
        "page": "5",
        "action": Buttons(5)
     }
 ]
return <React.Fragment>
{!isSingleTaskOpen && <TableCard columns={colums} data={data}/>}
{isSingleTaskOpen && <SingeleTask isAccept={false} closeTask={handleCloseTask} id={singleTaskId}/>}
</React.Fragment> 
}

export default OrdersInProgress;
