import React, { useEffect, useState } from "react";
import SingeleTask from "../../NewTask/taskDetails/singleTaskDetails";
//import TableCard from "../../Table/tableCard";
import TableCard from '../../../table/TableCard';
import ApiRequest from "src/Api/Requests/Apirequest";

const AssignedOrders = ()=>{
    const [isSingleTaskOpen,setShowSingleTask] = useState(false);
    const [singleTaskId,setSingleTaskId] = useState();

    const [assignedTask,setAssignedTask] = useState([]);


    const HandleAssignedTask = async ()=>{
        const result = await ApiRequest.getAssignedTask();
          var organisedData = [];
        result.data.forEach(element => {
             var dat ={
                    "#order": "committee-c15dw",
                    "ordername": element.task_title,
                    "title": 3,
                    "status": <span className="badge badge-danger">{element.task_status}</span>,
                    "created": '2021-07-7',
                    "deadline": '2021-07-7',
                    "service": "paraprhrasing",
                    "page": "5",
                    "action": Buttons(5)
                 }

                 organisedData.push(dat);

        });

        setAssignedTask(organisedData);


    }

    useEffect(()=>{
      HandleAssignedTask();
    },[]);

    const handleShowSingleTask=(id)=>{

          setSingleTaskId(id);
          setShowSingleTask(true);
    }


    const handleCloseTask=()=>{
       setSingleTaskId('');
       setShowSingleTask(false);
    }

 const ViewItemDetails =(id)=>{
    handleShowSingleTask(5)
    //alert(id);
}

const RejectOrder = (id)=>{
    alert(id);
}

const Buttons =(id)=>{
    return <React.Fragment>
         <button className="btn btn-danger" onClick={()=>RejectOrder(id)}>Reject</button>&nbsp;
         <button className="btn btn-info" onClick={()=>ViewItemDetails(id)}>Details</button>
    </React.Fragment>
}




 const columns = [

        {
            dataField: '#order',
           text: '#order',
        },
        {
            dataField: 'Order Name',
           text: 'ordername',
        },
        {
            dataField: 'Title',
           text: 'title',
        },
        {
            dataField: 'status',
           text: 'status',
        },
        {
            dataField: 'Created on',
           text: 'created',
        },
        {
            dataField: 'Deadline',
           text: 'deadline',
        },
        {
            dataField: 'Service',
           text: 'service',
        },
        {
            dataField: 'Pages',
           text: 'page',
        },

        {
            dataField: 'Action',
           text: 'action',

        },

 ]

 const data=[
     {
        "#order": "committee-c15dw",
        "ordername": "editor-ktsjo",
        "title": 3,
        "status": <span className="badge badge-danger">cancelled</span>,
        "created": '2021-07-7',
        "deadline": '2021-07-7',
        "service": "paraprhrasing",
        "page": "5",
        "action": Buttons(5)
     }
 ]
return <React.Fragment>
{!isSingleTaskOpen && <TableCard title={"assigned Orders"} columns={columns} products={[]}/>}
{isSingleTaskOpen && <SingeleTask closeTask={handleCloseTask} id={singleTaskId}/>}
</React.Fragment> 
}
export default AssignedOrders;
