import React, { Component } from 'react';
import { HashRouter, Route, Switch } from 'react-router-dom';
import './scss/style.scss';

const loading = (
  <div className="pt-3 text-center">
    <div className="sk-spinner sk-spinner-pulse"></div>
  </div>
)

// Containers
const TheLayout = React.lazy(() => import('./containers/TheLayout'));
const Login = React.lazy(() => import('./views/pages/login/Login'));
// Pages
/*

const Register = React.lazy(() => import('./views/pages/register/Register'));
const Page404 = React.lazy(() => import('./views/pages/page404/Page404'));
const Page500 = React.lazy(() => import('./views/pages/page500/Page500'));
*/
class App extends Component {

  constructor(props){
    super(props);
    this.state={
         username:"",
         profile:"",
         home:false
    }

  }


  handleProfile=(data)=>{

    const {username,profile} = data;
    //alert(username)
    this.setState({username:username,profile:profile,home:true});

 }

 route =async ()=>{

  //this.state.home == true?"":window.location.href="/#/auth";
    var tr= localStorage.getItem('tr');
    var ta = localStorage.getItem('ta');

      const check = await veryAccessTokens(ta,this.handleProfile);//authenTicateWebRefresh(ta,tr,this.handleProfile);



      if(check == true){
          this.setState({home:true})
      }else if( check == false){
        const checkRefresh = await requestRefreshToken(tr,this.handleProfile);
        if(checkRefresh == true){
          this.setState({home:true});
        }
      }
  if(!this.state.home){
      window.location.href="/#/login"
  }
 }

  componentDidMount=()=>{
    const url = window.location.href.split('/#/')[1];

    if(url === "auth/activate"){}
    else{

     if(url.split('?')[0] === "auth/activate"){
      console.log(url);
     }else{

       this.route();
     }
    }

  }

  render() {


    return (
      <HashRouter>
          <React.Suspense fallback={loading}>
            <Switch>
           <Route exact path="/login" name="Login Page" render={props => <Login {...props}/>} />
           <Route exact path="/" name="Login Page" render={props => <Login {...props}/>} />
           {/*
              <Route exact path="/register" name="Register Page" render={props => <Register {...props}/>} />
              <Route exact path="/404" name="Page 404" render={props => <Page404 {...props}/>} />
              <Route exact path="/500" name="Page 500" render={props => <Page500 {...props}/>} />
            */}
              <Route path="/home" name="Home" render={props => <TheLayout {...props}/>} />
            </Switch>
          </React.Suspense>
      </HashRouter>
    );
  }
}

export default App;
