import { useEffect, useState } from 'react';
import { UrlParams } from 'src/Api/auth/UrlParams';
import '../login/login.css';
import swal from "sweetalert";
import Loader from 'react-loader-spinner';
const Activate=()=>{
         const [Key,setKey] = useState('');
         const [btn,setBtn] = useState(false);
         const [save,SetSave] = useState(false);

    function showError(data){
        // alert(data);
          // swal("LOGIN FAILED",data,'error','1000');
          swal({
           title: "Activation Failed",
           text: data,
           icon:Â "error",
           timer: 3000
        });
        setBtn(false);
       }
    const ActivateAccount=()=>{
              SetSave(true);
              setTimeout(() => {
                  showError('wrong key')
                  SetSave(false);
              }, 2000);

    }

    useEffect(()=>{
       const Key = UrlParams('Key');
       if(!Key==false){
           setKey(Key);
           setBtn(true);

         ActivateAccount();
       }
       //alert(Key);
    },[])
    return <>   
    <div  id="myModal" class="show">
  <div style={{backgroundColor:"#f1f1",marginTop:"10%"}} class="modal-dialog modal-login">
      <div class="modal-content">
          <div class="modal-header">
              <div class="avatar">
                  <img src="https://www.tutorialrepublic.com/examples/images/avatar.png" alt="Avatar"/>
              </div>				
              <h4 class="modal-title">Activate Account</h4>	


          </div>
          <div class="modal-body">
              <form action="" method="post">

                  <div class="form-group">
                      <input type="number" class="form-control" name="key" placeholder="Activation Key" required="required"/>	
                  </div>        
                  <div class="form-group">
                      <button type="submit" disabled={btn} class="btn btn-primary btn-lg btn-block login-btn">Activate Account</button>
                  </div>
              </form>
          </div>

      </div>
  </div>
</div>  
{ save && <div className="spinner">


    <Loader
  type="Circles"
  color="#00BFFF"
  height={100}
  width={100}
  //3 secs
  />
  </div>
  } 
</>
}

export default Activate;
